import { useContext, useEffect, useState, useRef } from "react";
import { format } from 'timeago.js'
import { Link } from 'react-router-dom';
import { AuthContext } from "../../context/AuthContext";
import axios from "axios"
import {MoreVert} from '@material-ui/icons';
import { withStyles } from '@material-ui/core/styles';
import {styles} from './postStyle'
import CardHeader from '@material-ui/core/CardHeader'
import TextField from '@material-ui/core/TextField'
import Avatar from '@material-ui/core/Avatar'
import CommentSA from '../comment/commentSA';
import PropTypes from 'prop-types'

function Post({post, classes}) {
    const [like, setLike] = useState(post.likes.length);
    const [isLiked, setIsLiked] = useState(false);
    const [user, setUser] = useState({});
    const [text, setText] = useState('')
    const desc = useRef();

    const { user: currentUser } = useContext(AuthContext);
    const PF = process.env.REACT_APP_PUBLIC_FOLDER;

    useEffect(() => {
        setIsLiked(post.likes.includes(currentUser._id));
      }, [currentUser._id, post.likes]);

    useEffect(() => {
        const fetchUser = async () => {
            const res = await axios.get(`/users?userId=${post.userId}`)
            setUser(res.data);
        };

        fetchUser();
    }, [post.userId])

    const handleChange = event => {
        setText(event.target.value)
      }

    // submit a comment
    const submitHandler = async (e) => {
        e.preventDefault();
        const newComment = {
          userId: user._id,
          description: desc.current.value,
        };
        try {
          await axios.post("/posts/"+ post._id + "/comment", { desc : newComment.description, userId: currentUser._id});
          // refresh the page after posting something
          window.location.reload();
        } catch (err) {console.log(err)}
    };

    const likeHandler = () => {
        try {
            axios.put("/posts/" + post._id + "/like", { userId: currentUser._id });
        } catch (err) {}
        setLike(isLiked ? like - 1 : like + 1);
        setIsLiked(!isLiked);
    };

    return (
        <div className={classes.post}>
            <div className={classes.postWrapper}>
                <div className={classes.postTop}>
                    <div className={classes.postTopLeft}>
                        <Link to={`profile/${user.username}`}>
                            <img src={user.profilePicture ? PF + user.profilePicture : PF+'person/noAvatar.png'} alt="" className={classes.postProfileImg}/>
                        </Link>
                        <span className={classes.postUsername}>
                            { user.username }
                        </span>
                        <span className={classes.postDate}>{format(post.createdAt)}</span>
                    </div>
                    <div className={classes.postTopRight}>
                        <MoreVert/>
                    </div>
                </div>
                <div className={classes.postCenter}>
                    <span className={classes.postText}>{post?.desc}</span>
                    <img src={PF + post.img} alt="" className={classes.postImg}/>
                </div>
                <div className={classes.postBottom}>
                    <div className={classes.postBottomLeft}>
                        <img src={`${PF}like.png`} alt="" className={classes.likeIcon}  style={{ display: "none" }} onClick={likeHandler} />
                        <img src={`${PF}heart.png`} alt="" className={classes.likeIcon} onClick={likeHandler} />
                        <span className={classes.postLikeCounter}>{like} likes</span>
                    </div>
                    <div className={classes.postBottomRight} style={{ display: "none" }}>
                        <div className={classes.postCommentText}>{post.comment } comments</div>
                    </div>
                </div>
                <form onSubmit={submitHandler}>
                <div className="commentsContainer">
                <CardHeader
              avatar={
                <Avatar className={classes.smallAvatar} src={'/api/users/photo/'+ currentUser._id}/>}
              title={ 
              <TextField
                multiline
                onChange={handleChange}
                ref={desc}
                placeholder="Write something ..."
                className={classes.commentField}
                margin="normal"
                />
              }
              className={classes.cardHeader}
                />
                <button className={classes.shareButton} style={{ align: "right" }} type="submit">Share</button>
                {post.comments.map((c) => {
                    return <CommentSA key={c._id} post={c}/>
                })}
                </div></form>
            </div>
        </div>
    )
}


Post.propTypes = {
    post: PropTypes.object.isRequired,
    onRemove: PropTypes.func.isRequired
  }
export default withStyles(styles)(Post);
